(function () {
  'use strict';

  const id = decodeURIComponent('132321');

  function runOnce(fn) {
    const uniqId = 'BEXT_UNIQ_ID_' + id;
    if (window[uniqId]) {
      return;
    }
    window[uniqId] = true;
    fn && fn();
  }

  function addElement({ tag, attrs = {}, to = document.body }) {
    const el = document.createElement(tag);
    Object.assign(el, attrs);
    to.appendChild(el);
    return el;
  }

  function loadScript(src) {
    return new Promise((resolve, reject) => {
      addElement({
        tag: 'script',
        attrs: {
          src,
          type: 'text/javascript',
          onload: resolve,
          onerror: reject,
        },
      });
    });
  }

  runOnce(() => {
      loadScript('https://unpkg.com/vconsole@latest/dist/vconsole.min.js')
          .then(() => new window.VConsole());
  });

})();
