meta.source:
import { name, id, version } from '@bext/context';
import { runOnce, addElement, loadScript, addStyle, checkUpdate } from '@bext/util';
import { alook_install, via_install, buildMethods } from '@bext/browser';

runOnce(() => {
    console.log('这里将只会被执行一次');
    console.log(`当前的脚本名称：${name}，ID：${id}，版本：${version}`);

    // 向 body 中添加一个元素
    const el = addElement({
        tag: 'div',
        attrs: { id: 'myDiv' },
        to: document.body
    });

    // 加载远程脚本，也可以用 await
    loadScript('https://cdn.jsdelivr.net/npm/jquery/dist/jquery.min.js')
        .then(() => console.log('加载成功！'))
        .catch(() => console.log('加载失败'));

    // 向 head 添加一个 css 样式
    addStyle(`
        #ad {
            display: none;
        }
    `);

    // 以7天为间隔检查脚本更新
    checkUpdate(7).then((url) => {
        if (url && confirm('当前脚本有更新，是否前往更新')) {
            window.open(url);
        }
    });

    const browser = buildMethods({ alook_install, via_install });
    // 给 alook, via 安装一个脚本
    browser('install', {/* xxx */ });
});

meta.build:
(function () {
  'use strict';

  const id = decodeURIComponent('example');
  const name = decodeURIComponent('%E7%A4%BA%E4%BE%8B%E8%84%9A%E6%9C%AC');
  const version = decodeURIComponent('7');
  const bextHome = decodeURIComponent('https%3A%2F%2Fbext.ketra.fun');

  function detectBrowser() {
    return window.via && (window.via.cmd || window.via.openSettings)
      ? 'via'
      : window.alook && window.alook.addon
      ? 'alook'
      : window.mbrowser && window.mbrowser.getBrowsreInfo
      ? 'x'
      : window.bz && window.bz.addScript
      ? 'bz'
      : window.sharkbrowser && window.sharkbrowser.installAddon
      ? 'shark'
      : window.lit && window.lit.addon
      ? 'lit'
      : undefined;
  }

  function base64(str) {
    return window.btoa(unescape(encodeURIComponent(str)));
  }

  function runOnce(fn) {
    const uniqId = 'BEXT_UNIQ_ID_' + id;
    if (window[uniqId]) {
      return;
    }
    window[uniqId] = true;
    fn && fn();
  }

  function addElement({ tag, attrs = {}, to = document.body }) {
    const el = document.createElement(tag);
    Object.assign(el, attrs);
    to.appendChild(el);
    return el;
  }

  function loadScript(src) {
    return new Promise((resolve, reject) => {
      addElement({
        tag: 'script',
        attrs: {
          src,
          type: 'text/javascript',
          onload: resolve,
          onerror: reject,
        },
      });
    });
  }

  function addStyle(css) {
    return addElement({
      tag: 'style',
      attrs: {
        textContent: css,
      },
      to: document.head,
    });
  }

  const LAST_CHECK_KEY = `BEXT_LAST_CHECK_KEY_${id}`;
  async function checkUpdate(day = 7) {
    const lastCheck = Number(localStorage.getItem(LAST_CHECK_KEY));
    localStorage.setItem(LAST_CHECK_KEY, Date.now());

    if (
      !Number.isNaN(lastCheck) &&
      (Date.now() - lastCheck) / (24 * 60 * 60) <= Math.min(3, day)
    ) {
      return;
    }
    try {
      const response = await fetch(
        `https://cdn.jsdelivr.net/gh/ikkz/bext@master/meta/${id}.json`,
      );
      const meta = await response.json();
      if (meta.version != version) {
        return `${bextHome}/meta/${id}`;
      }
    } catch (error) {}
  }

  const errorCatch = (fn) => {
    return (...args) => {
      try {
        return {
          code: 0,
          result: fn?.(...args),
        };
      } catch (error) {
        return { code: error.bextCode ?? -1 };
      }
    };
  };

  const buildMatch = (match, sep, all) => {
    if (match && match.length) {
      return match.join(sep);
    }
    return all;
  };

  const via_install = errorCatch((meta) =>
    window.via.addon(
      base64(
        JSON.stringify({
          id: meta.id,
          name: meta.name,
          author: meta.author,
          url: buildMatch(meta.match, ',', '*'),
          code: base64(meta.build),
        }),
      ),
    ),
  );

  const alook_install = errorCatch((meta) =>
    window.alook.addon(
      base64(
        encodeURIComponent(
          JSON.stringify({
            id: meta.id,
            name: meta.name,
            author: meta.author,
            url: buildMatch(meta.match, '@@', '*'),
            code: base64(meta.build),
          }),
        ),
      ),
    ),
  );

  const buildMethods = (impls) => {
    const browser = detectBrowser();
    return (method, ...args) => {
      const fn = impls[`${browser}_${method}`];
      return fn ? fn(...args) : { code: -1 };
    };
  };

  runOnce(() => {
      console.log('这里将只会被执行一次');
      console.log(`当前的脚本名称：${name}，ID：${id}，版本：${version}`);

      // 向 body 中添加一个元素
      addElement({
          tag: 'div',
          attrs: { id: 'myDiv' },
          to: document.body
      });

      // 加载远程脚本，也可以用 await
      loadScript('https://cdn.jsdelivr.net/npm/jquery/dist/jquery.min.js')
          .then(() => console.log('加载成功！'))
          .catch(() => console.log('加载失败'));

      // 向 head 添加一个 css 样式
      addStyle(`
        #ad {
            display: none;
        }
    `);

      // 以7天为间隔检查脚本更新
      checkUpdate(7).then((url) => {
          if (url && confirm('当前脚本有更新，是否前往更新')) {
              window.open(url);
          }
      });

      const browser = buildMethods({ alook_install, via_install });
      // 给 alook, via 安装一个脚本
      browser('install', {/* xxx */ });
  });

})();
